selTextureReduction:AddItem( STR( "UI_LOWEST"), 4);
selTextureReduction:AddItem( STR( "UI_LOW"), 3);
selTextureReduction:AddItem( STR( "UI_NORMAL"), 2);
selTextureReduction:AddItem( STR( "UI_HIGH"), 1);
selTextureReduction:AddItem( STR( "UI_HIGHEST"), 0);

Event.OnSelChange:
luaOption:ChangedOption( true);

local _val = selTextureReduction:GetItemValue( selTextureReduction:GetCurSel());
local _index = selTextureReduction:GetItemFromValue( gameoption:SetTextureReduction( _val));
selTextureReduction:SetCurSel( _index);

