maiet.SCRIPT:


function NPC_103003:Init( NPCID )
	--[[  USERDATA 
	1 : Pain 데미지 누적
	--]]
	NPC_ReserveUserData( NPCID, 1 )

	local MaxHP 										= NPC_103003.MaxHP

	NPC_103003.LimitForPain								= MaxHP/10 	-- pain 데미지 누적
	NPC_103003.TalIdPain								= 210300302	-- 데미지 누적 아픔

	NPC_103003.TalIdTwoHand						 	    = 210300312 -- 양팔 합치기
	NPC_103003.TalIdBackAttack						 	= 210300313 -- 뒷발길질
	NPC_103003.TalIdBugSummon						 	= 210300319 -- 벌레소환
	NPC_103003.TalIdTwoHand2					 	    = 210300324 -- 양팔 합치기2
	NPC_103003.TalIdTwoHand3					 	    = 210300325 -- 양팔 합치기3
	NPC_103003.TalIdFireMud					 	        = 210300326 -- 진흙폭파
	NPC_103003.TalIdTwoHand4					 	    = 210300327 -- 양팔 합치기4
	NPC_103003.TalIdTwoHand5					 	    = 210300328 -- 양팔 합치기5

	NPC_103003.BuffIdStar								= 40839     -- 볼락 반짝이
	NPC_103003.BuffIdArmBug								= 40851     -- 볼락 어깨벌레
	NPC_103003.NpcIdBugR								= 103006    -- 빨강벌레

end

function NPC_103003:OnSpawn(Spawn)
	this:GainBuff( NPC_103003.BuffIdStar )
	this:GainBuff( NPC_103003.BuffIdArmBug )
end

function NPC_103003:OnBPartRecover()  
	this:SetUserData( 1, 0 )
end

--	뒷발길질
function NPC_103003:BackAttack( ThisNPC, Enemy )
	local Dist = ThisNPC:DistanceTo( Enemy )
	local IsRear = Enemy:IsRear( ThisNPC )

	if Dist < 600 and IsRear then
		ThisNPC:UseTalentSelf( NPC_103003.TalIdBackAttack )
	end 
end

-- 잡아서 내려치기, 진흙소환 어그로 바꾸기
function NPC_103003:OnFinishAggroChange( ThisActor, Enemy )
	AsNPC(ThisActor):Aggro( "random", 0 )
end

-- 빨강벌레 소환 
function NPC_103003:OnStartSummonBugR( ThisActor, Enemy )
	local ThisNPC = AsNPC( ThisActor )
	SummonMinion( ThisNPC, nil, NPC_103003.NpcIdBugR, 1, "", -700, 700 )
end

function NPC_103003:AccDamage( ThisNPC, HitInfo )
	local currentTalent = ThisNPC:GetCurrentTalent()

	local Pain =  ThisNPC:GetUserData( 1 ) + HitInfo.Damage
	ThisNPC:SetUserData( 1, Pain )

	-- pain 예외 처리.
	if currentTalent == NPC_103003.TalIdTwoHand or
		currentTalent == NPC_103003.TalIdBugSummon or
		currentTalent == NPC_103003.TalIdTwoHand2 or
		currentTalent == NPC_103003.TalIdTwoHand3 or
		currentTalent == NPC_103003.TalIdFireMud or
		currentTalent == NPC_103003.TalIdTwoHand4 or
		currentTalent == NPC_103003.TalIdTwoHand5 or
		ThisNPC:IsCooldown( NPC_103003.TalIdPain ) then
		return HitInfo
	end

	if	Pain > NPC_103003.LimitForPain then
		ThisNPC:ClearJob()
		ThisNPC:UseTalentSelf( NPC_103003.TalIdPain )
		ThisNPC:SetUserData( 1 , 0 )
	end

	return HitInfo
end


-- 전체
function NPC_103003:OnHitCapsule_1_0(HitInfo)
	return NPC_103003:AccDamage( this, HitInfo )
end
function NPC_103003:OnHitCapsule_1_1(HitInfo)
	return NPC_103003:AccDamage( this, HitInfo )
end
