	return NPC_103003:AccDamage( this, HitInfo )
end
function NPC_103003:OnHitCapsule_1_7(HitInfo)
	return NPC_103003:AccDamage( this, HitInfo )
end
function NPC_103003:OnHitCapsule_1_8(HitInfo)
	return NPC_103003:AccDamage( this, HitInfo )
end
