function NPC_103126:Init( NPCID ) NPC_ReserveUserData( NPCID, 1 ) end
function NPC_103126:BreakRandomPart( ThisNPC, Enemy) ThisNPC:ClearJob()
local n = math.random( 1, 3 ) if not ThisNPC:CheckBPart( n ) then ThisNPC:BreakPart( n, Enemy ) end end
function NPC_103126:OnHitCapsule_1_0(HitInfo) local HPPer = this:GetHP() / this:GetMaxHP() local State = this:GetUserData( 1 )
if HPPer < 0.75 and State < 1 and ChkMFForHitReaction( HitInfo ) then NPC_103126:BreakRandomPart( this, HitInfo.Attacker ) this:SetUserData( 1, 1 ) elseif HPPer < 0.5 and State < 2 then NPC_103126:BreakRandomPart( this, HitInfo.Attacker ) this:SetUserData( 1, 2 ) elseif HPPer < 0.25 and State < 3 then NPC_103126:BreakRandomPart( this, HitInfo.Attacker ) this:SetUserData( 1, 3 ) end end
