maiet.SCRIPT:
function NPC_609601:Init( NPCID )
	--[[  USERDATA 
	1 : 턱 
	2 : 레이저 포인터
	3 : 오른손
	4 : 왼손
	5 : Pain
	--]]
	NPC_ReserveUserData( NPCID, 5 )

	NPC_609601.LimitForBreakFirstHand			= 100
	NPC_609601.LimitForBreakHand 				= NPC_609601.MaxHP/20
	NPC_609601.LimitForBreakJaw					= NPC_609601.MaxHP/2
	NPC_609601.LimitForBreakLazerPointer 		= NPC_609601.MaxHP/10
	NPC_609601.LimitForPain						= NPC_609601.MaxHP/10
	NPC_609601.PartsDestroyBonusDamage			= NPC_609601.MaxHP/6

	NPC_609601.TalIdDash						= 260960106
	NPC_609601.TalIdTailAttack 					= 260960109
	NPC_609601.TalIdTwoHandPress				= 260960111

	NPC_609601.TalIdPain1						= 260960104
	NPC_609601.TalIdPain2						= 260960105
	NPC_609601.TalIdThreat						= 260960103
	NPC_609601.BufIdMindControl					= 40182

	NPC_609601.PartsIdxJaw							= 1
	NPC_609601.PartsIdxLazerPointer				= 2
	NPC_609601.PartsIdxLeftHand					= 3
	NPC_609601.PartsIdxRightHand					= 4

	NPC_609601.TalIdBPartReaction				= 260960130
	NPC_609601.TalIdBPartAttack					= 190011

--	NPC_609601.NpcIdDummy						= 109107
end

function NPC_609601:OnBPartRecover()
	for i=1, 5 do
		this:SetUserData( i, 0)
	end -- for
end

function NPC_609601:TailAttack( ThisNPC, Enemy )
	local Dist = ThisNPC:DistanceTo( Enemy )
	local IsRear = Enemy:IsRear( ThisNPC )

	if Dist > 300 and IsRear then
		ThisNPC:UseTalentSelf( NPC_609601.TalIdTailAttack )
	end
end

function NPC_609601:ProcessBreakHand( ThisNPC, PartsIdx, HitInfo )
	ThisNPC:ClearJob()
	ThisNPC:UseTalentSelf( NPC_609601.TalIdPain2 )
	ThisNPC:SetUserData( PartsIdx, 0 )
	ThisNPC:BreakPart( PartsIdx, HitInfo.Attacker )

	HitInfo.Damage = HitInfo.Damage + NPC_609601.PartsDestroyBonusDamage
	return HitInfo
end

-- 데미지 처리로직
function NPC_609601:AccDamage( ThisNPC, PartsIdx, HitInfo )
	-- Damage Adjust
	-----------------
	local bLeftHand = ThisNPC:CheckBPart( NPC_609601.PartsIdxLeftHand )
	local bRightHand = ThisNPC:CheckBPart( NPC_609601.PartsIdxRightHand )

	if PartsIdx == NPC_609601.PartsIdxLeftHand and  bLeftHand then
		HitInfo.Damage = HitInfo.Damage*2
		--GLog( "Damage Amplied " .. HitInfo.Damage .."\n" )
	elseif PartsIdx == NPC_609601.PartsIdxRightHand and bRightHand then
		HitInfo.Damage = HitInfo.Damage*2
		--GLog( "Damage Amplied " .. HitInfo.Damage .."\n" )
	end

	-- Handle break-parts-weapon reaction
	-------------------
	if HitInfo.TalentID == NPC_609601.TalIdBPartAttack then
		ThisNPC:ClearJob()
		ThisNPC:UseTalentSelf( NPC_609601.TalIdBPartReaction )
		return HitInfo
	end

	-- Damage Accumulate
	----------------------
	local DmgAcc = 0
	if  PartsIdx > 0 and PartsIdx <= 4 then
		DmgAcc = ThisNPC:GetUserData( PartsIdx ) + HitInfo.Damage	
		ThisNPC:SetUserData( PartsIdx, DmgAcc )
	end

	DmgAcc = ThisNPC:GetUserData( 5 ) + HitInfo.Damage	
	ThisNPC:SetUserData( 5, DmgAcc )

	--[[
	GLog( "-- Process Acc Damage ( P:" .. PartsIdx .. 
		", D:" .. HitInfo.Damage .. ", 1:" .. ThisNPC:GetUserData( 1 ) .. 
		", 2:" .. ThisNPC:GetUserData( 2 ) .. ", 3:" .. ThisNPC:GetUserData( 3 ) .. ", 4:" .. ThisNPC:GetUserData( 4 ) .. ", 5:" .. ThisNPC:GetUserData( 5 ) .. " ) --\n"  )
	--]]

	-- Reaction
	-----------

	local bCooldown = ThisNPC:IsCooldown( NPC_609601.TalIdPain1 ) 
	local CurrentTalent = ThisNPC:GetCurrentTalent()
	local bTalentFilter = ( CurrentTalent == NPC_609601.TalIdTwoHandPress ) or ( CurrentTalent == NPC_609601.TalIdDash )

	-- First Break
	if ( PartsIdx == NPC_609601.PartsIdxLeftHand or PartsIdx == NPC_609601.PartsIdxRightHand ) and
		( not( bLeftHand or bRightHand ) ) and 
		( not bCooldown ) and  
		( not HitInfo.Attacker:IsNPC() ) and
		ThisNPC:GetUserData( PartsIdx ) > NPC_609601.LimitForBreakFirstHand then


		return NPC_609601:ProcessBreakHand( ThisNPC, PartsIdx, HitInfo )
	end

	-- Left Hand
	if PartsIdx == NPC_609601.PartsIdxLeftHand and
		( not bLeftHand ) and
		( not bCooldown ) and	
		( not HitInfo.Attacker:IsNPC() ) and
		ThisNPC:GetUserData( PartsIdx ) > NPC_609601.LimitForBreakHand then

		return NPC_609601:ProcessBreakHand( ThisNPC, PartsIdx, HitInfo )
	end

	-- Right Hand
	if PartsIdx == NPC_609601.PartsIdxRightHand and
		( not bRightHand ) and
		( not bCooldown ) and
		( not HitInfo.Attacker:IsNPC() ) and
		ThisNPC:GetUserData( PartsIdx ) > NPC_609601.LimitForBreakHand then

		return NPC_609601:ProcessBreakHand( ThisNPC, PartsIdx, HitInfo )
	end

	--[[ Do not apply breaking-jaw logic now. No fun and No beauty.
	--  Jaw
	if PartsIdx == NPC_609601.PartsIdxJaw and
		( not ThisNPC:CheckBPart( NPC_609601.PartsIdxJaw ) ) and
		( not bCooldown ) and
		ThisNPC:GetUserData( PartsIdx ) > NPC_609601.LimitForBreakHand then

		ThisNPC:ClearJob()
		ThisNPC:UseTalentSelf( NPC_609601.TalIdPain2	)
		ThisNPC:SetUserData( PartsIdx, 0 )
		ThisNPC:BreakPart( PartsIdx, HitInfo.Attacker )
		return HitInfo
	end
	--]]

	--  Pain
	if  ( not bCooldown ) and 
		( not bTalentFilter ) and
		ThisNPC:GetUserData( PartsIdx ) > NPC_609601.LimitForPain then

		ThisNPC:ClearJob()
		ThisNPC:UseTalentSelf( NPC_609601.TalIdPain1	)
		ThisNPC:UseTalentSelf( NPC_609601.TalIdThreat	)
		ThisNPC:SetUserData( PartsIdx, 0 )
		return HitInfo
	end

	return HitInfo
end

function NPC_609601:OnFinishMindControl( ThisNPC, Enemy )
	ThisNPC:RemoveBuff( NPC_609601.BufIdMindControl )
end

